   :d3fend/kb-reference-title "CAR-2013-02-008: Simultaneous Logins on a Host",
   :db/ident :d3fend/Reference-SimultaneousLoginsOnAHost_MITRE,
   :rdf/type [:d3fend/ExternalKnowledgeBase :owl/NamedIndividual],
